    <!-- payment script -->
    <script>
      $(function(){
        function openRazorpay(amount, description) {
          var newQuantPay = {
                      "key": "rzp_live_CFDShpkrgvuvxb",
                      "amount": amount,
                      "name": "RAINFUND",
                      "description": description,
                      "image": "https://rainfund.ai/img/logo/header-logo.svg",
                      "handler": function (response){
                            console.log('adding hide .rf-response-container');
                            $('.rf-response-container').removeClass('hidden')
                            console.log('removing hide .rf-response-loading');
                            $('.rf-response-loading').removeClass('hidden')
                            console.log('adding hide .rf-response-status');
                            $('.rf-response-status').addClass('hidden')
                            console.log('adding hide .rf-response-message');
                            $('.rf-response-message').addClass('hidden')
                            $('.rf-response-info').addClass('hidden')

                            console.log('response ', JSON.stringify(response));
                            const postData = {
                                "razorpay_payment_id": response.razorpay_payment_id,
                                "description": description
                            };
                            $.post( "../razorpay/soq-payment-capture.php", postData, function( postResponse ) {
                                console.log('adding hide .rf-response-loading');
                                $('.rf-response-loading').addClass('hidden')
                                console.log('removing hide .rf-response-status');
                                $('.rf-response-status').removeClass('hidden')
                                console.log('removing hide .rf-response-message');
                                $('.rf-response-message').removeClass('hidden')
                                $('.rf-response-info').removeClass('hidden')
                                console.log('postResponse ', JSON.stringify(postResponse));

                                console.log('setting data ');
                                $('.rf-response-status').text(postResponse.status)
                                $('.rf-response-message').text(postResponse.message)
                            });
                      },
                      "theme": {
                          "color": "#5a00ff"
                      }
                  };
          var quantPay = new Razorpay(newQuantPay);
          quantPay.open();
        }

        $('.enroll-btn').click(function(e) {
            var amount = 1450000;   // in paise
            var batchid = $(this).data("batchid");
            var description = "School Of Quant | "+batchid
            if (batchid=="1") {
                openRazorpay(amount, description);
            }else{
                alert('Hi, this batch is full. Please check other batches.');
            }
            e.preventDefault();
        });

        $('.rf-response-container').click(function(e){
            $('.rf-response-container').addClass('hidden')
        })


      });
    </script>
